mapper.update:
UPDATE timesheet_entries
SET
    start_time            = #{startTime}
    , end_time            = #{endTime}
    , note                = #{note}
    , updated_at          = NOW()
    , break_minutes       = #{breakMinutes}
    , duration_minutes    = #{durationMinutes}
    , working_minutes     = #{workingMinutes}
    , version             = version + 1
    , holiday_work        = #{holidayWork}
    , work_location       = #{workLocation}
    , irregular_work_type = #{irregularWorkType}
    , irregular_work_desc = #{irregularWorkDesc}
    , irregular_work_data = #{irregularWorkData}
    , late_time           = #{lateTime}
    , late_desc           = #{lateDesc}
    , early_time          = #{earlyTime}
    , early_desc          = #{earlyDesc}
    , paid_leave          = #{paidLeave}
WHERE
    id = #{id}
    AND version = #{version}

